INSERT INTO `config_info` VALUES (195, 'module-share-config.yml', 'PUBLIC_CONFIG_GROUP', 'spring:\r\n    zipkin:\r\n        base-url: http://127.0.0.1:9411\r\n        sender:\r\n            type: web\r\n    #设置Sleuth的抽样收集概率,1=100%,因为我们目前是测试环境所以用1\r\n    sleuth:\r\n        sampler:\r\n            probability: 1\r\n#请求处理的超时时间\r\nribbon:\r\n    ReadTimeout: 10000\r\n    ConnectTimeout: 10000\r\n# 服务内部消费接口\r\nfeign:\r\n    client:\r\n        config:\r\n            default:\r\n                connectTimeout: 10000\r\n                loggerLevel: basic\r\n                readTimeout: 10000\r\n    compression:\r\n        request:\r\n            enabled: true\r\n            mime-types: text/xml,application/xml,application/json\r\n            min-request-size: 2048\r\n        response:\r\n            enabled: true\r\n    sentinel:\r\n        enabled: true\r\n    okhttp:\r\n        enabled: true\r\n    httpclient: \r\n        enabled: false\r\n# 暴露监控端点\r\nmanagement:\r\n    endpoint:\r\n        health:\r\n            show-details: always\r\n    endpoints:\r\n        web:\r\n            exposure:\r\n                include: \'*\'', '3052bd9c58a396799676c7cf18959048', '2020-11-23 13:44:14', '2020-11-30 21:29:12', NULL, '127.0.0.1', '', 'xqoo-cloud-namespace-asdfghjk-dev', '', '', '', 'yaml', '');
INSERT INTO `config_info` VALUES (199, 'public-redis-config.yml', 'PUBLIC_CONFIG_GROUP', 'spring:\r\n    redis:\r\n        database: 1\r\n        host: 127.0.0.1\r\n        lettuce:\r\n            pool:\r\n                max-active: 100\r\n                max-idle: 10\r\n                max-wait: 20000\r\n                min-idle: 1  \r\n        password: A123456bexqoo                    \r\n        port: 6379\r\n        timeout: 10000\r\n        # 最大重定向次数，不能超过节点总数，用以限制访问失败后转其他节点访问限制，集群属性，单点无视\r\n        max-redirects: 3\r\n        # 集群节点扫描间隔，单位毫秒\r\n        scan-interval: 1000\r\n        # 此项为集群redis节点地址，不是集群忽略即可，注意下方key和值之间不能有空格\r\n        cluster-node:\r\n            - node-host: 127.0.0.1\r\n              node-port: 6379\r\n            - node-host: 192.168.0.106\r\n              node-port: 6379', 'fa03901ca91b91d36f24a54b6ed5306e', '2020-11-23 13:57:16', '2020-11-23 14:30:56', NULL, '127.0.0.1', '', 'xqoo-cloud-namespace-asdfghjk-dev', '', '', '', 'yaml', '');
INSERT INTO `config_info` VALUES (202, 'public-mysql-config.yml', 'PUBLIC_CONFIG_GROUP', 'spring:\r\n    datasource:\r\n        driverClassName: com.mysql.cj.jdbc.Driver\r\n        druid:\r\n            filter:\r\n                stat:\r\n                    enabled: true\r\n                    log-slow-sql: true\r\n                    merge-sql: true\r\n                    slow-sql-millis: 1000\r\n                wall:\r\n                    config:\r\n                        multi-statement-allow: true\r\n            initialSize: 5\r\n            maxActive: 20\r\n            maxEvictableIdleTimeMillis: 900000\r\n            maxWait: 60000\r\n            minEvictableIdleTimeMillis: 300000\r\n            minIdle: 10\r\n            password: Zyj123456.\r\n            statViewServlet:\r\n                enabled: true\r\n            testOnBorrow: false\r\n            testOnReturn: false\r\n            testWhileIdle: true\r\n            timeBetweenEvictionRunsMillis: 60000\r\n            url: jdbc:mysql://127.0.0.1:3306/xqoo_system_console?useUnicode=true&characterEncoding=utf8&zeroDateTimeBehavior=convertToNull&useSSL=true&serverTimezone=GMT%2B8\r\n            username: root\r\n            validationQuery: SELECT 1 FROM DUAL\r\n            webStatFilter:\r\n                enabled: true\r\n        type: com.alibaba.druid.pool.DruidDataSource', '3848787208efd22339ce47be6d35796a', '2020-11-23 13:58:21', '2020-11-23 13:58:53', NULL, '127.0.0.1', '', 'xqoo-cloud-namespace-asdfghjk-dev', '', '', '', 'yaml', '');
INSERT INTO `config_info` VALUES (212, 'public-xqoo-config.yml', 'PUBLIC_CONFIG_GROUP', 'xqoo:\r\n  auth:\r\n    # jwt 加密秘钥\r\n    jwt-secret-key: secretKey:88945.939\r\n    # jwt令牌默认失效时间，实际可根据程序中自定，单位s\r\n    jwt-expire: 600\r\n    # token失效，单位s\r\n    token-expire: 10000\r\n    # token 刷新时间，小于此时间时刷新token持续时间，单位 s, \r\n    # 为0时表示不刷新，到期即失效token\r\n    # 最小60，最大不超过失效时间一半\r\n    token-refresh-limit: 6000\r\n    # 是否开启时区校验，不同时区账号不允许登录\r\n    time-zone-check: true\r\n    # 时间误差，当登录请求发送时间与服务器接收时间超过此误差值时，不允许登录，关闭时区校验后此项不可用，单位：秒\r\n    time-exact: 30\r\n    # 单一登录限制,true为开启单一登录，一处登录登出其他地方\r\n    login-single: true\r\n    # 单一登录类型，all-为全平台不论登录来源，source-每个来源只能登录一个\r\n    login-single-type: all \r\n    # 是否开启登录错误次数过多锁定\r\n    login-err-lock: \r\n      active: true\r\n      # 最大错误次数 小于等于0为不限制\r\n      max-error-time: 8\r\n      # 错误锁定时间 分钟，-1则为不自动解锁\r\n      lock-time: 30\r\n      # 密码错误几次开启验证， 0和-1表示不开启\r\n      need-check-error-time: 3\r\n      # 错误验证码失效时间 分钟, 小于等于0时默认5分钟\r\n      error-code-expire: 5\r\n    # 登录方式开开关\r\n    login-type-switch:\r\n      # 账号密码模式\r\n      - type: PASSWORD\r\n        active: true\r\n        # 邮件模式\r\n      - type: EMAIL\r\n        active: false\r\n      # 面部识别模式\r\n      - type: FACE\r\n        active: false\r\n      # 指纹识别模式\r\n      - type: FINGER\r\n        active: false\r\n      # QRCODE模式\r\n      - type: QRCODE\r\n        active: false\r\n      # 手机验证码模式\r\n      - type: PHONE\r\n        active: false\r\n      # 第三方登录模式\r\n      - type: THIRDPARTY\r\n        active: false\r\n  secret:\r\n    # aes加密的秘钥，必须16位，否则会报错\r\n    aes-key: EbQT9S2Ou-Lm3Oq0', '5bbb02f9d6186ea34277799f062e5c5a', '2020-11-23 16:47:22', '2020-12-29 10:49:07', NULL, '127.0.0.1', '', 'xqoo-cloud-namespace-asdfghjk-dev', '', '', '', 'yaml', '');
INSERT INTO `config_info` VALUES (245, 'public-xqoo-gateway-config.yml', 'PUBLIC_CONFIG_GROUP', 'xqoo:\r\n  gateway:\r\n    # 目标黑名单域名，ip或路径，支持通配符, 没有host或path不填即可\r\n    target-black-list:  \r\n      - host: \'www.baidu.con\'\r\n        path: \'/*\'\r\n      - path: \'/helloworld/**\'\r\n    # ip来源黑名单, host必须有，port可以是多个，支持通配符\r\n    remote-black-list:\r\n      - host: \'192.168.0.3\'\r\n        port: \r\n          - \'**\'\r\n    # 不需要登录的路径，支持通配符\r\n    no-login-filter:\r\n      - path: /v2/api-docs/**\r\n      - path: /author/**\r\n    # 系统占用的返回状态，不做统一拦截\r\n    filter-http-status:\r\n      - status-code: 400\r\n        reason: 请求资源已失效\r\n      - status-code: 401\r\n        reason: 请求资源需要登录\r\n      - status-code: 402\r\n        reason: 请求资源需要付费\r\n      - status-code: 404\r\n        reason: 请求资源未找到\r\n      - status-code: 405\r\n        reason: 提交方式不正确或表单数据验证不通过\r\n      - status-code: 406\r\n        reason: 请求不予以通过\r\n      - status-code: 408\r\n        reason: 请求超时\r\n      - status-code: 412\r\n        reason: 密码输入错误次数过多\r\n      - status-code: 421\r\n        reason: 当前请求人已停用\r\n      - status-code: 423\r\n        reason: 请求来源账户已冻结或被锁定\r\n      - status-code: 429\r\n        reason: 请求次数过于频繁\r\n      - status-code: 502\r\n        reason: 请求的地址错误\r\n      - status-code: 504\r\n        reason: 请求地址访问超时\r\n      - status-code: 510\r\n        reason: 请求来源ip已被拉黑\r\n      - status-code: 511\r\n        reason: 请求地址已被拉黑', '0a9c244e54bceecbc2c0296a74a4f404', '2020-11-28 13:38:18', '2020-12-05 12:26:59', NULL, '127.0.0.1', '', 'xqoo-cloud-namespace-asdfghjk-dev', '', '', '', 'yaml', '');
