INSERT INTO `config_info` VALUES (247, 'xqoo-cloud-code-generator.yml', 'DEFAULT_GROUP', 'server:\r\n    port: 9009\r\nspring:\r\n    datasource:\r\n        druid:\r\n            # 使用默认连接属性请酌情注释此三项\r\n            #password: Zyj123456.\r\n            url: jdbc:mysql://127.0.0.1:3306/xqoo_code_generator?useUnicode=true&characterEncoding=utf8&zeroDateTimeBehavior=convertToNull&useSSL=true&serverTimezone=GMT%2B8\r\n            #username: root\r\n    redis:\r\n        database: 1\r\ncode-gen:\r\n    download-base-path: D:/home/codeGen\r\n    default-author: xqoo-code-gen\r\n    data-base-type:\r\n        - type: mysql\r\n          driver: com.mysql.jdbc.Driver\r\n          prefix: jdbc:mysql://\r\n          port: \'3306\'\r\n          properties: useUnicode=true&characterEncoding=utf8&zeroDateTimeBehavior=convertToNull&useSSL=true&serverTimezone=GMT%2B8\r\n    template:\r\n        - label: controller.java\r\n          value: controller.java\r\n          # 主要用于生成不同代码获取模板不同时展示，多个类型用|隔开，any表示所有类型都显示此模板\r\n          type: tableGen\r\n          # url只实际模板相对路径，一个模板类型可包含多个模板路径\r\n          url: \r\n            - vm/java/controller.java.vm\r\n        - label: service.java\r\n          value: service.java\r\n          type: tableGen\r\n          url: \r\n            - vm/java/service.java.vm\r\n            - vm/java/serviceImpl.java.vm\r\n        - label: entity.java\r\n          value: entity.java\r\n          type: tableGen\r\n          url: \r\n            - vm/java/entity.java.vm\r\n        - label: mapper.java\r\n          value: mapper.java\r\n          type: tableGen\r\n          url: \r\n            - vm/java/mapper.java.vm\r\n        - label: mapper.xml\r\n          value: mapper.xml\r\n          type: tableGen\r\n          url: \r\n            - vm/xml/mapper.xml.vm\r\n        - label: microService\r\n          value: microService\r\n          type: microService\r\n          url:\r\n            - vm/microServiceTemplate/src/main/java/com/xqoo/Application.java.vm\r\n            - vm/microServiceTemplate/src/main/resource/bootstrap.yml.vm\r\n            - vm/microServiceTemplate/src/main/resource/logback-spring.xml.vm\r\n            - vm/microServiceTemplate/Dockerfile.vm\r\n            - vm/microServiceTemplate/nacos-application.yml.vm\r\n            - vm/microServiceTemplate/readme.md.vm\r\n            - vm/microServiceTemplate/pom.xml.vm\r\n        - label: singleEntity\r\n          value: singleEntity\r\n          type: singleEntity\r\n          url:\r\n            - vm/java/singleEntity.java.vm', '27363a2115eb9fef4112d4a0cbfd4b25', '2020-12-23 11:36:19', '2021-01-04 18:31:02', NULL, '127.0.0.1', '', 'xqoo-cloud-namespace-asdfghjk-dev', '', '', '', 'yaml', '');

