INSERT INTO `his_config_info` VALUES (212, 317, 'public-xqoo-config.yml', 'PUBLIC_CONFIG_GROUP', '', 'xqoo:\r\n  auth:\r\n    # jwt 加密秘钥\r\n    jwt-secret-key: secretKey:88945.939\r\n    # jwt令牌默认失效时间，实际可根据程序中自定，单位s\r\n    jwt-expire: 600\r\n    # token失效，单位s\r\n    token-expire: 10000\r\n    # token 刷新时间，小于此时间时刷新token持续时间，单位 s, \r\n    # 为0时表示不刷新，到期即失效token\r\n    # 最小60，最大不超过失效时间一半\r\n    token-refresh-limit: 6000\r\n    # 单一登录限制,true为开启单一登录，一处登录登出其他地方\r\n    login-single: true\r\n    # 单一登录类型，all-为全平台不论登录来源，source-每个来源只能登录一个\r\n    login-single-type: all \r\n    # 是否开启登录错误次数过多锁定\r\n    login-err-lock: \r\n      active: true\r\n      # 最大错误次数 小于等于0为不限制\r\n      max-error-time: 8\r\n      # 错误锁定时间 分钟，-1则为不自动解锁\r\n      lock-time: 30\r\n      # 密码错误几次开启验证， 0和-1表示不开启\r\n      need-check-error-time: 3\r\n      # 错误验证码失效时间 分钟, 小于等于0时默认5分钟\r\n      error-code-expire: 5\r\n    # 登录方式开开关\r\n    login-type-switch:\r\n      # 账号密码模式\r\n      - type: PASSWORD\r\n        active: true\r\n        # 邮件模式\r\n      - type: EMAIL\r\n        active: false\r\n      # 面部识别模式\r\n      - type: FACE\r\n        active: false\r\n      # 指纹识别模式\r\n      - type: FINGER\r\n        active: false\r\n      # QRCODE模式\r\n      - type: QRCODE\r\n        active: false\r\n      # 手机验证码模式\r\n      - type: PHONE\r\n        active: false\r\n      # 第三方登录模式\r\n      - type: THIRDPARTY\r\n        active: false\r\n  secret:\r\n    # aes加密的秘钥，必须16位，否则会报错\r\n    aes-key: EbQT9S2Ou-Lm3Oq0', 'b9177c8f773c792e794a959b527da6dd', '2020-12-29 10:49:06', '2020-12-29 10:49:07', NULL, '127.0.0.1', 'U', 'xqoo-cloud-namespace-asdfghjk-dev');
