mapper.select:
select usr.user_id as "userId", usr.del_flag as "delFlag", usr.login_id as "loginId", usr.user_status as "userStatus", (case usr.user_status when 0 then '正常' when 1 then '封禁' when 2 then '停用' else '未知' end) as "userStatusName", usr.user_type as "userType", (case usr.user_type when 99 then '超级管理员' when 88 then '后台用户' when 10 then 'web端用户' when 9 then '临时用户' else '未知' end) as "userTypeName", false as "checked", count(usrRole.role_id) as "roleCount", usr.user_name as "userName", usr.user_phone as "userPhone", usr.user_email as "userEmail", usr.last_login_time as "lastLoginTime", usr.profile_url as "profileUrl", usr.create_by as "createBy", usr.create_date as "createData", usr.update_by as "updateBy", usr.update_date as "updateDate", usr.remark_tips as "remartTips" from sys_user usr left join sys_user_role usrRole on usr.user_id = usrRole.user_id where usr.del_flag = 0
and usr.user_id = #{bo.userId}
and usr.login_id like concat('%',#{bo.loginId},'%')
and usr.user_status = #{bo.userStatus}
and usr.user_type = #{bo.userType}
and usr.user_name like concat('%',#{bo.userName},'%')
and usr.user_phone like concat('%',#{userPhone},'%')
and usr.user_email like concat('%',#{userEmail},'%')
group by usr.user_id

select
  user_id, del_flag, login_id, user_status, user_type, user_name, password, salt, last_login_time, profile_url, create_by, create_date, update_by, update_date, remark_tips
from xqoo_system_console.sys_user


        and user_id = #{userId}


        and del_flag = #{delFlag}


        and login_id = #{loginId}


        and user_status = #{userStatus}


        and user_type = #{userType}


        and user_name = #{userName}


        and password = #{password}


        and salt = #{salt}


        and last_login_time = #{lastLoginTime}


        and profile_url = #{profileUrl}


        and create_by = #{createBy}


        and create_date = #{createDate}


        and update_by = #{updateBy}


        and update_date = #{updateDate}


        and remark_tips = #{remarkTips}
